	def build(A,input_shape):'\n    Creates the variables of the layer:\n    hash_keys, hash_values, bin_ids, bin_values, feature_offsets and self.output_size.\n    ';B=False;A.partition=Partition();A.stitch=Stitch();C=A.add_variable('hash_keys',initializer=A._hash_keys_initializer,shape=[A._n_feature],dtype=tf.int64,trainable=B);D=A.add_variable('hash_values',initializer=A._hash_values_initializer,shape=[A._n_feature],dtype=tf.int64,trainable=B);E=tf.lookup.KeyValueTensorInitializer(C,D);A.hash_map=tf.lookup.StaticHashTable(E,-1);A.bin_ids=A.add_variable('bin_ids',initializer=A._bin_ids_initializer,shape=[A._n_feature*(A._n_bin+1)],dtype=tf.int64,trainable=B);A.bin_values=A.add_variable('bin_values',initializer=A._bin_values_initializer,shape=[A._n_feature*(A._n_bin+1)],dtype=tf.float32,trainable=B);A.feature_offsets=A.add_variable('feature_offsets',initializer=A._feature_offsets_initializer,shape=[A._n_feature],dtype=tf.int64,trainable=B);A.built=True
	def call(A,inputs,**X):
		'Looks up `keys` in a table, outputs the corresponding values.\n\n    Implements MDL inference where inputs are intersected with a hash_map.\n    Part of the inputs are discretized using twml.mdl to produce a mdl_output SparseTensor.\n    This SparseTensor is then joined with the original inputs SparseTensor,\n    but only for the inputs keys that did not get discretized.\n\n    Args:\n      inputs: A 2D SparseTensor that is input to MDL for discretization.\n        It has a dense_shape of [batch_size, input_size]\n      name: A name for the operation (optional).\n    Returns:\n      A `SparseTensor` of the same type as `inputs`.\n      Its dense_shape is [shape_input.dense_shape[0], 1 << output_bits].\n    ';B=inputs
		if isinstance(B,tf.SparseTensor):B=twml.SparseTensor.from_tf(B)
		assert isinstance(B,twml.SparseTensor);J=B.ids;E=B.indices;D=B.values;F=A.hash_map.lookup(E);G=tf.not_equal(F,tf.constant(-1,tf.int64));K=tf.cast(G,tf.int32);D,L,M=A.partition(K,D,tf.where(G,F,E));C,N=L;O,P=D;A.non_mdl_keys=C;H,Q=libtwml.ops.mdl(N,P,A.bin_ids,A.bin_values,A.feature_offsets);I=tf.size(A.bin_ids,out_type=tf.int64);R=tf.subtract(A.output_size,I);C=tf.add(tf.floormod(C,R),I);A.mdl_out_keys=H;S=A.stitch([O,Q],[C,H],M);T,U=S;V=tf.to_int64(B.dense_shape[0]);W=[V,A.output_size];return twml.SparseTensor(J,U,T,W).to_tf()
